package com.gu.adapters.queue

import com.gu.core.models.DeletionEvent
import org.scalatest.matchers.should.Matchers
import org.scalatest.flatspec.AnyFlatSpec

class DeletionEventTest extends AnyFlatSpec with Matchers {

  "DeletionEvent" should "deserialise deletion event correctly" in {
    val eventData =
      """
        |{
        |  "Type" : "Notification",
        |  "MessageId" : "d8392d1e-8c11-5a2b-ab02-7fd3dc51552b",
        |  "TopicArn" : "arn:aws:sns:eu-west-1:942464564246:com-gu-identity-account-deletions-PROD",
        |  "Subject" : "Account deletion event",
        |  "Message" : "{\"userId\":\"18467226\",\"eventType\":\"DELETE\"}",
        |  "Timestamp" : "2018-01-08T10:54:56.297Z",
        |  "SignatureVersion" : "1",
        |  "Signature" : "",
        |  "SigningCertURL" : "blah",
        |  "UnsubscribeURL" : "blah"
        |}
      """.stripMargin

    DeletionEvent.userId(eventData) shouldBe Some("18467226")

  }

  it should "return None when no user id available" in {
    val eventData =
      """
        |{
        |  "Type" : "Notification",
        |  "MessageId" : "d8392d1e-8c11-5a2b-ab02-7fd3dc51552b",
        |  "TopicArn" : "arn:aws:sns:eu-west-1:942464564246:com-gu-identity-account-deletions-PROD",
        |  "Subject" : "Account deletion event",
        |  "Message" : "",
        |  "Timestamp" : "2018-01-08T10:54:56.297Z",
        |  "SignatureVersion" : "1",
        |  "Signature" : "",
        |  "SigningCertURL" : "blah",
        |  "UnsubscribeURL" : "blah"
        |}
      """.stripMargin

    DeletionEvent.userId(eventData) shouldBe None

  }

  it should "return None when json is invalid" in {
    val eventData =
      """
        |}
      """.stripMargin

    DeletionEvent.userId(eventData) shouldBe None

  }

}
